import React, { useState } from 'react'
import { GIT_URL } from '../config'

const ReactLevel2 = () => {
  const [query, setQuery] = useState('')
  const [results, setResults] = useState({})
  const [totalCount, setTotalCount] =useState(0)

  const httpRequest = () => {
    fetch(GIT_URL + query,
      { method: 'GET' })
      .then(function (response) {
        return response.json()
      })
      .catch(function () {
        console.log('error')
      })
      .then(function (json) {
        setResults(json.items)
        setTotalCount(json.total_count)
      })
  }
console.log(results)
  return (
    <div className='jumbotron jumbotronHome mt-5'>
      <h1 className='display-3'>
        Github user search
      </h1>
      <p className='lead'>
        Create an input text in which as the user types in, launch a search against Github users and return a result
        list.
      </p>
      <input className="form-control" placeholder="Search" type="text"
             onChange={(e) => {
               setQuery(e.target.value)
             }}
      />
      <button type="button" className="btn bg-gradient-primary" onClick={httpRequest}>Search</button>
      <hr className='my-4'/>

      <div className="container">
        <div className="row">


          <div className="col-md-4">
            <div className="card card-profile card-plain">
              <div className="z-index-1">
                <div className="position-relative w-25">
                  <div className="blur-shadow-avatar">
                    {/*<img className="avatar avatar-xxl shadow-lg" src="./assets/img/kit/pro/team-4.jpg" />*/}
                  </div>
                </div>
              </div>
              <div className="card-body ps-0">
                <h5 className="mb-0">Alec Thompson</h5>
                <p className="text-muted">CEO / Co-Founder</p>
                <p>
                  And I love you like Kanye loves Kanye. We need to restart the human foundation.
                </p>
                <button type="button" className="btn-icon-only btn-simple btn btn-lg btn-twitter" data-toggle="tooltip"
                        data-placement="bottom" title="Follow me!">
                </button>
              </div>
            </div>


          </div>
        </div>
      </div>
    </div>

  )
}

export default ReactLevel2
