import React, { useState } from 'react'
import { GIT_URL } from '../config'

const ReactLevel2 = () => {
  const [query, setQuery] = useState('')
  const [items, setItems] = useState({})
  const [totalCount, setTotalCount] =useState(0)

  const httpRequest = () => {
    fetch(GIT_URL + query,
      { method: 'GET' })
      .then(function (response) {
        return response.json()
      })
      .catch(function () {
        console.log('error')
      })
      .then(function (json) {
        setItems(json.items)
        setTotalCount(json.total_count)
      })
  }

  return (
    <div className='jumbotron jumbotronHome mt-5'>
      <h1 className='display-3'>
        Github user search
      </h1>
      <p className='lead'>
        Create an input text in which as the user types in, launch a search against Github users and return a result
        list.
      </p>
      <input className="form-control" placeholder="Search" type="text"
             onChange={(e) => {
               setQuery(e.target.value)
             }}
      />
      <button type="button" className="btn bg-gradient-primary" onClick={httpRequest}>Search</button>
